import randomdef fastPower(g, A, N):    a = g    b = 1    while A > 0:        if A % 2 == 1:            b = (b * a) % N        a = (a * a) % N         # print (a)        A = A // 2    # print (b)    return bdef int2bin(integer):        binString = ''        while integer > 0:                mod = integer % 2                binString = str(mod) + binString                integer //= 2        while len(binString) % 8 != 0:                binString = '0' + binString        return binStringdef bin2intlong(binary):        total = 1        for place in range(len(binary) - 1, 0, -1):                tempvalue = int(binary[place])                total += tempvalue * (2 ** (place))        return totaldef bin2int(binary):        return int(binary, 2)def msg2bin(message):        return ''.join(['{0:08b}'.format(ord(x)) for x in message])def bin2msg(binary):        return ''.join(chr(int(binary[(i * 8):(i * 8 + 8)], 2)) for i in range(len(binary) // 8))def msg2int(message):        return bin2int(msg2bin(message))def int2msg(integer):        return bin2msg(int2bin(integer))def MsgSenderBob():    message = open('input.txt', "r")    line = ''    for newline in message.readlines():        line = line + str(newline)  # Read the text and turn the text into individual characters    message.close()    line = line.split()    # ## Read lines from the file    p = int(line[0])      g = int(line[1])    A = int(line[2])    a = int(line[3])    r = random.randint(0, 10e5)  # # Select a random integer for r    message = 'Bravo'    msg = msg2int(message)  # # Convert the string into integer variable    C1 = fastPower(g, r, p)  # # Calculate the value of C1 and C2    C2 = msg * fastPower(A, r, p) % p    # ## Write all the variables to the 'output' file in line    output = open("output.txt", "w")    output.write (str(p))    output.write ('\n')    output.write (str(g))    output.write ('\n')    output.write (str(a))    output.write ('\n')    output.write (str(C1))    output.write ('\n')    output.write (str(C2))    output.close ()    MsgSenderBob()def MsgReceiverAlice():    message = open('output.txt', "r")    line = ''    for newline in message.readlines():        line = line + str(newline)  # Read the text and turn the text into individual characters    message.close()    line = line.split()    # ## Read lines from the file which provided by Bob    p = int(line[0])      # g = int(line[1])    a = int(line[2])    C1 = int(line[3])    C2 = int(line[4])        plain = fastPower(C1, a * (p - 2), p) * C2 % p  # # Calculate the received message from Bob    plaintext = int2msg(plain)  # # Convert the message into readable English    print("The text is :", plaintext)  # # Print MsgReceiverAlice()